document.getElementById("password").classList.add("input-password"),
    document.getElementById("toggle-password").classList.remove("d-none");
const passwordInput=document.getElementById("password"),
    togglePasswordButton=document.getElementById("toggle-password");
togglePasswordButton.addEventListener("click",togglePassword);
function togglePassword()
{"password"===passwordInput.type?
    (passwordInput.type="text",togglePasswordButton.setAttribute("aria-label","Hide password.")):
    (passwordInput.type="password",togglePasswordButton.setAttribute
    ("aria-label","Show password as plain text. Warning: this will display your password on the screen."))};

